import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = ({ loggedIn, email }) => {
  const navigate = useNavigate();
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [showLoginButton, setShowLoginButton] = useState(true);
  const [showEmailInput, setShowEmailInput] = useState(false);
  const [inputEmail, setInputEmail] = useState('');
  const [inputPassword, setInputPassword] = useState('');

  const onButtonClick = () => {
    if (loggedIn) {
      // Handle logout functionality
    } else {
      // Show email input and hide login button
      setShowEmailInput(true);
      setShowLoginButton(false);
      setEmailError('');
    }
  };

  const handleEmailChange = (event) => {
    setInputEmail(event.target.value);
    setEmailError('');
  };

  const handlePasswordChange = (event) => {
    setInputPassword(event.target.value);
    setPasswordError('');
  };

  const handleSubmit = () => {
    if (!inputEmail) {
      setEmailError('Please enter your email');
      return;
    }

    if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(inputEmail)) {
      setEmailError('Please enter a valid email');
      return;
    }

    // Show password input field after valid email
    setShowEmailInput(false);
    setPasswordError('');
  };

  const handleLoginSubmit = () => {
    if (!inputPassword) {
      setPasswordError('Please enter your password');
      return;
    }

    // Example authentication logic goes here...

    // After successful login logic, navigate or update loggedIn state
  };

  return (
    <div className="mainContainer">
      <div className="titleContainer">
        <div>Welcome!</div>
      </div>
      <div>This is the home page.</div>
      <div className="buttonContainer">
        {showLoginButton && (
          <input
            className="inputButton"
            type="button"
            onClick={onButtonClick}
            value={loggedIn ? 'Log out' : 'Log in'}
          />
        )}
        {loggedIn ? (
          <div>Your email address is {email}</div>
        ) : (
          showEmailInput && (
            <div className="inputContainer">
              <input
                type="email"
                value={inputEmail}
                onChange={handleEmailChange}
                placeholder="Enter your email"
              />
              {emailError && <div className="error">{emailError}</div>}
              <input
                className="inputButton"
                type="button"
                onClick={handleSubmit}
                value="Next"
              />
            </div>
          )
        )}
        {!showEmailInput && (
          <div className="inputContainer">
            <input
              type="password"
              value={inputPassword}
              onChange={handlePasswordChange}
              placeholder="Enter your password"
            />
            {passwordError && <div className="error">{passwordError}</div>}
            <input
              className="inputButton"
              type="button"
              onClick={handleLoginSubmit}
              value="Log in"
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
