import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = ({ loggedIn, email }) => {
  const navigate = useNavigate();
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [inputEmail, setInputEmail] = useState('');
  const [inputPassword, setInputPassword] = useState('');

  const handleLoginButtonClick = () => {
    setShowLoginForm(true);
  };

  const handleEmailChange = (event) => {
    setInputEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setInputPassword(event.target.value);
  };

  const handleLoginSubmit = () => {
    // Example authentication logic goes here...
    navigate('/dashboard'); // Navigate to dashboard or update loggedIn state on successful login
  };

  return (
    <div className="mainContainer">
      <div className="contentContainer">
        {!showLoginForm && (
          <div className="titleContainer">
            <div>Welcome!</div>
            <div style={{ fontSize: '0.8em' }}>Need help finding a fitness buddy?</div>
          </div>
        )}
        {!showLoginForm && (
          <div className="buttonContainer">
            <input
              className="inputButton"
              type="button"
              onClick={handleLoginButtonClick}
              value="Log in"
              style={{ fontSize: '3.5em', padding: '20px 30px', marginTop: '30px' }} // Adjusted margin-top
            />
          </div>
        )}
        {showLoginForm && (
          <div className="loginFormContainer">
            <div style={{ fontSize: '1.2em', marginBottom: '10px' }}>Log In Here</div>
            <div style={{ fontSize: '0.8em', marginBottom: '20px' }}>Enter username and password below.</div>
            <input
              type="text"
              className="inputField"
              value={inputEmail}
              onChange={handleEmailChange}
              placeholder="Enter your email"
            />
            <br />
            <input
              type="password"
              className="inputField"
              value={inputPassword}
              onChange={handlePasswordChange}
              placeholder="Enter your password"
            />
            <br />
            <input
              className="inputButton"
              style={{ marginLeft: 'auto', marginRight: 'auto', display: 'block' }}
              type="button"
              onClick={handleLoginSubmit}
              value="Submit"
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
